import React,{useState} from 'react';
import {BsX} from 'react-icons/bs';

import { DragDropContext } from 'react-beautiful-dnd';
import './App.css';
import data from './data';

function App() {
  const[list,setList]=useState(data)
  const[newTask,setNewTask]=useState('');
  const handleDelete=(id)=>{
    console.log('id',id)
    const newList=list.filter(item=>list.indexOf(item)!==id)

    setList(newList)
    console.log('new',newList)
  }
  const handleAddTask=()=>{
    console.log('new task',newTask)
    const [updatedList]=list.push(newTask)
    //console.log('updated list',updatedList)
    setList(updatedList)
  }
  return (
    <div className="App">
      <h2>To Do List</h2>
      <div className='task'>
        <input type='text' className='inputbox' value={newTask}onChange={(e)=>setNewTask(e.target.value)}/>
        <button className='Addbtn' onClick={handleAddTask}>Add</button>
      </div>
      <div className='section'>
        {list.map((item,index)=>{
          return(
            <div className='item' key={index}>
            <p  >{item}</p>
            <BsX className='icon' onClick={()=>handleDelete(index)}/>
            </div>
          )

        })}

      </div>
    </div>
  );
}

export default App;
