import React,{useState} from 'react';
import {BsX} from 'react-icons/bs';

import { DragDropContext } from 'react-beautiful-dnd';
import './App.css';
import data from './data';

function App() {
  const[list,setList]=useState(data)
  const handleDelete=(id)=>{
    const newList=list.flter(item=>item.id!==id)
    setList(newList)
  }
  return (
    <div className="App">
      <h2>To Do List</h2>
      <div className='section'>
        {list.map((item,index)=>{
          return(
            <div className='item'>
            <p  key={index}>{item}</p>
            <BsX className='icon' onClick={handleDelete(index)}/>
            </div>
          )

        })}

      </div>
    </div>
  );
}

export default App;
